targets.expr:
(
  sort_desc(
    topk(
      10,
      rate(pg_stat_statements_calls_total{datname=~"$Database", instance=~"$Instance"}[$__interval_ms])
    )
  )
  or irate(pg_stat_statements_calls_total{datname=~"$Database", instance=~"$Instance"}[$__interval_ms])
)
* on (queryid, instance)
group_left(query)
max by (instance, queryid, query) (
  pg_stat_statements_query_id{instance=~"$Instance"}
)

topk(
  10,
  (
    rate(pg_stat_statements_seconds_total{datname=~"$Database", instance=~"$Instance"}[$__interval_ms])
    /
    rate(pg_stat_statements_calls_total{datname=~"$Database", instance=~"$Instance"}[$__interval_ms])
  )
)
* on (queryid, instance)
group_left(query)
max by (instance, queryid, query) (
  pg_stat_statements_query_id{instance=~"$Instance"}
)

(
  sort_desc(
    topk(
      5,
      sum by(queryid, datname, user) (
        irate(pg_stat_statements_seconds_total{instance=~"$Instance"}[$__interval_ms])
      )
    )
  )
  unless (
    topk(
      20,
      sum by(queryid, datname, user) (
        irate(pg_stat_statements_time_milliseconds{instance=~"$Instance"}[$__interval_ms])
      )
    )
  )
)
* on (queryid)
group_left(instance, query)
max by (instance, queryid, query) (
  pg_stat_statements_query_id{instance=~"$Instance"}
)

