  (
    pg_stat_statements_seconds_total{datname=~"$Database", instance=~"$Instance"}
    /
    pg_stat_statements_calls_total{datname=~"$Database", instance=~"$Instance"}
  )
)
*
on(queryid, instance)
group_left(query)
pg_stat_statements_query_id{instance=~"$Instance"}

sum by (query) (
  pg_stat_statements_calls_total{datname=~"$Database", instance=~"$Instance"}
*
on(queryid, instance)
group_left(query)
pg_stat_statements_query_id{instance=~"$Instance"}
)
